// Allow importing CSS and preprocessor files in TypeScript
declare module '*.css'
declare module '*.module.css'
declare module '*.scss'
declare module '*.module.scss'
declare module '*.sass'
declare module '*.module.sass'

// Optionally allow other static assets if needed
declare module '*.svg'
declare module '*.png'
declare module '*.jpg'
declare module '*.jpeg'
declare module '*.gif'
